#include <main.h>

bool	env_cmp(const char *lhs, const char *rhs)
{
	uint32_t	i;

	i = 0;
	while (lhs[i] && lhs[i] != '=' && lhs[i] == rhs[i])
		i += 1;
	return (rhs[i] == '=' && (lhs[i] == '=' || lhs[i] == 0));
}
